using System.ComponentModel.DataAnnotations;namespace Travelingyu.Models{    public class Pelanggan    {        [Key]        public string KodePelanggan { get; set; }        [Required]        public string NamaPelanggan { get; set; }        [Required]        public string JenisKelamin { get; set; }        [Required]        public string DaerahAsal { get; set; }
        [Required]        public string NomorKtp { get; set; }        [Required]        public string NomorTelpon { get; set; }        [Required]        public string Type { get; set; }    }}